link_pattern = '(?!https)(?:(?:(?:http:)?//)|(?:/))(?:[a-zA-Z]|[0-9]|[$-_@.&+]|[!*\(\),]|(?:%[0-9a-fA-F][0-9a-fA-F]))+'
tailq = Queue.Queue()
fetch_pattern = '.+\.(?:(?:gif)|(?:jpe?g)|(?:png)|(?:js))$'

def tail_forever(fn):
    p = subprocess.Popen(["tail", "-f", fn], stdout=subprocess.PIPE)
    while 1:
        line = p.stdout.readline()
        tailq.put(line)
        if not line:
            break

threading.Thread(target=tail_forever, args=(fn,)).start()


def read_line(line):
    line = line.split()
    # ignore requests from localhost, the idea is to ignore requests from myself
    # sldo only process GET requests
    if line[2] != '127.0.0.1' and line[5] == 'GET':
        content = line[9]
        if content in ('application/pkix-crl', '-', 'image/gif', 'image/x-icon', 'application/javascript',
                       'application/x-javascript', 'application/json', 'image/png', 'image/jpeg',
                       'application/octet-stream', 'application/gzip', 'application/xml'):
            return
        url = line[6]
        if content in ('text/html', 'text/css'):
            print(url)
            netloc = urlparse(url).netloc
            text = requests.get(url, proxies=proxies).text
            for match in re.findall(link_pattern, text):
                if match.startswith('//') and len(match) > 2:
                    fetch_url = 'http:' + match
                elif match.startswith('/'):
                    if match.endswith('>'):
                        continue
                    fetch_url = 'http://' + netloc + match
                else:
                    fetch_url = match
                # print(match, len(match))
                fetch(fetch_url)
        else:
            print('Unrecognized content', content, url)


